import React, { useState ,useEffect} from 'react';
import {BrowserRouter as Router,Routes, Route} from 'react-router-dom';
import TaskManager from './components/TaskManager';
import Login from './components/login';
import CustomNavbar from './components/navbar';
import Register from './components/Register';
import Home from './components/home';
import 'bootstrap/dist/css/bootstrap.min.css';
import "./style.css";



function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      <div style={{display:'flex', flexDirection:'column'}}>
        <CustomNavbar isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />
        <h1 style={{position:'relative',marginBottom:'15px',fontWeight:'bolder', marginTop:'25px'}}>TODO LIST</h1>
        <div class="badge-base LI-profile-badge" data-locale="en_US" data-size="medium" data-theme="dark" data-type="VERTICAL" data-vanity="thanmai-jalakam-590255231" data-version="v1"><a class="badge-base__link LI-simple-link" href="https://in.linkedin.com/in/thanmai-jalakam-590255231?trk=profile-badge">Thanmai Jalakam</a></div>
        <script src="https://platform.linkedin.com/badges/js/profile.js" async defer type="text/javascript"></script>              
        
        <Routes>
          <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} ></Route>
          <Route path="/taskmanager" element={<TaskManager />} />
          <Route path="/register" element={<Register />} />
          <Route path='/' element={<Home/>}/>
          {/*<Route path='/' element={<Login setIsAuthenticated={setIsAuthenticated}/>}/>*/}
        </Routes>
      </div>
    </Router>

);
}

export default App;
