import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar, Nav, Button } from 'react-bootstrap';

function CustomNavbar({ isAuthenticated, setIsAuthenticated }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    navigate('/');
  };

  const handleTasksClick = () => {
    if (!isAuthenticated) {
      alert('Login to see the tasks');
      navigate('/login');
    } else {
      navigate('/dashboard');
    }
  };

  return (
    <Navbar bg="light" expand="lg" fixed='top'>
      <Navbar.Brand href="#" style={{fontSize: 'bold',fontStyle:'initial'}}>Todo List</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link as={Link} to="/login" fontStyle>Login</Nav.Link>
          <Nav.Link as="button" onClick={handleTasksClick}>Tasks</Nav.Link>
          {isAuthenticated && (
            <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
          )}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default CustomNavbar;
