import React, { useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar, Nav, Button } from 'react-bootstrap';

function CustomNavbar({ isAuthenticated, setIsAuthenticated }) {
  const navigate = useNavigate();
  useEffect(()=>{
    console.log(isAuthenticated);
  },[isAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    navigate('/');
  };

  const handleTasksClick = () => {
    if (!isAuthenticated) {
      alert('Login to see the tasks');
      navigate('/login');
    } else {
      navigate('/taskmanager');
    }
  };

  return (
    
    <Navbar bg="light" expand="lg" sticky='top' >
      <Navbar.Brand href='/' style={{fontSize: 'bold',fontStyle:'initial', fontWeight:'bolder'}}>Todo List</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link as={Link} to="/login" style={{fontSize:'bold', color:'black',fontWeight:'bold'}}>Login</Nav.Link>
          <Nav.Link  onClick={handleTasksClick} style={{fontWeight:'bold', color:'black', fontsize:'bold'}}>Tasks</Nav.Link>
          {isAuthenticated && (
            <Nav.Link onClick={handleLogout} style={{fontWeight:'bold', color:'black', fontsize:'bold', alignContent:'right'}}>Logout</Nav.Link>
          )}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default CustomNavbar;
