import React, { useState } from 'react';
import axios from 'axios';
//import Dashboard from './Dashboard';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');

  const handleRegister = async () => {
    try {
      await axios.post('http://localhost:5000/api/register', { username, password });
      alert('User registered successfully');
      setPassword('')
      setUsername('')
    } catch (error) {
      console.error('Registration failed', error.response.data.error);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/login', { username, password });
      setToken(response.data.token);
      alert('Login successful');
      setUsername("");
      setPassword('');
    } catch (error) {
      setUsername('');
      setPassword('');
      alert('Invalid credentials', error.response.data.error);
      console.error('Login failed', error.response.data.error);
    }
  };

  return (
    
    <div>
      <h1>MERN Stack Authentication App</h1>

      <label>Username: <input type="text" value = {username} onChange={(e) => setUsername(e.target.value)} /></label><br></br>
      <label>Password: <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} /></label><br></br>
      <button onClick={handleRegister}>Register</button><br></br>
      <button onClick={handleLogin}>Login</button>
      {/*token && <p>Token: {token}</p>}
      {username && <p>username  : {username}</p>*/}
      
    </div>
    
  );
}

export default App;
